package vav.codility.arrays.strings;

import org.junit.Test;

import static org.junit.Assert.*;

public class StringContainsBinarySubstrTest {

    @Test
    public void check() {
       assertEquals(true, new StringContainsBinarySubstr().hasAllCodes("00110",2));
       assertEquals(true, new StringContainsBinarySubstr().hasAllCodes("",19));
       assertEquals(true, new StringContainsBinarySubstr().hasAllCodes("0000000001011100", 4));
    }

}
